import TaskList from "./TaskList";
import { useState } from "react";

function TaskItem({title,id,state,toggleComplete}){
    const [input, setInput] = useState("");
    const [allTasks, setAllTasks] = useState([]);

    function handleComplete(){
        toggleComplete(id);
    }
    function deleteTask(id){
        let updatedTaskList = allTasks.map((task) => {
            if (task.date === id) {
                updatedTaskList.splice(id,1);
            } 
            })
        // ? Updating the list of tasks in the allTasks array
        setAllTasks(updatedTaskList);
    }
    

    return (
        <div>
            <input type="checkbox" onChange={handleComplete}/>
            <span style={{textDecoration:state?"line-through":"none"}} >{title}</span>
            { <button onClick={deleteTask} >Delete</button> }
        </div>
    )
}

export default TaskItem;
